import{_ as e,c as a,o as i,ae as t}from"./chunks/framework.C2WZnq5v.js";const u=JSON.parse('{"title":"羊棒骨","description":"","frontmatter":{},"headers":[],"relativePath":"stew/羊棒骨.md","filePath":"stew/羊棒骨.md"}'),o={name:"stew/羊棒骨.md"};function c(p,l,b,n,r,h){return i(),a("div",null,l[0]||(l[0]=[t('<h1 id="羊棒骨" tabindex="-1">羊棒骨 <a class="header-anchor" href="#羊棒骨" aria-label="Permalink to &quot;羊棒骨&quot;">​</a></h1><p><strong>作者：于雨欣</strong></p><h2 id="备料" tabindex="-1">备料 <a class="header-anchor" href="#备料" aria-label="Permalink to &quot;备料&quot;">​</a></h2><ol><li><label><input type="checkbox"> 羊棒骨(让老板切块)</label></li><li><label><input type="checkbox"> 葱</label></li><li><label><input type="checkbox"> 花椒</label></li><li><label><input type="checkbox"> 麻椒</label></li><li><label><input type="checkbox"> 辣椒</label></li><li><label><input type="checkbox"> 小茴香</label></li><li><label><input type="checkbox"> 冰糖</label></li><li><label><input type="checkbox"> 姜(尽量多点)</label></li><li><label><input type="checkbox"> 好人家辣火锅底料</label></li><li><label><input type="checkbox"> 生抽</label></li><li><label><input type="checkbox"> 耗油</label></li><li><label><input type="checkbox"> 高度白酒</label></li></ol><h2 id="烹调工序" tabindex="-1">烹调工序 <a class="header-anchor" href="#烹调工序" aria-label="Permalink to &quot;烹调工序&quot;">​</a></h2><ol><li><p>将准备好的羊棒骨加入葱姜料酒花椒焯水去腥，焯好后温水洗净备用。</p></li><li><p>锅中加油烧热，加入大量葱姜爆香，后加入大量小茴香，花椒，麻椒，辣椒再次爆香。</p></li><li><p>加入好人家火锅底料，适量生抽，耗油，几粒冰糖调味，料炒化后烹入高度白酒。</p></li><li><p>加入羊棒骨轻微翻炒，加入足量开水，炖煮2个小时。出锅前可根据口味决定是否加盐。</p></li></ol><h2 id="附录" tabindex="-1">附录 <a class="header-anchor" href="#附录" aria-label="Permalink to &quot;附录&quot;">​</a></h2><ol><li>火锅底料可以选择其他品牌，最好牛油比重高的。</li><li>加开水时一次加够，中途尽量不额外加水，加的话也要加开水。</li><li>建议准备吸管以方便吸骨髓。</li><li>爱吃辣，吃棒骨的家人们最好也别一次吃太多，由于其特性，吃多了容易刺激肠胃。</li><li>吃完棒骨后可以顺便涮点其他爱吃的食材。</li><li>本食谱根据美食作家王刚的麻辣羊蝎子教程改编</li></ol>',8)]))}const _=e(o,[["render",c]]);export{u as __pageData,_ as default};
