import{_ as t,c as i,o as p,ae as l}from"./chunks/framework.C2WZnq5v.js";const u=JSON.parse('{"title":"青菜丸子粉丝汤@nz","description":"","frontmatter":{},"headers":[],"relativePath":"stew/青菜丸子粉丝汤@nz.md","filePath":"stew/青菜丸子粉丝汤@nz.md"}'),a={name:"stew/青菜丸子粉丝汤@nz.md"};function o(c,e,n,r,s,_){return p(),i("div",null,e[0]||(e[0]=[l('<h1 id="青菜丸子粉丝汤-nz" tabindex="-1">青菜丸子粉丝汤@nz <a class="header-anchor" href="#青菜丸子粉丝汤-nz" aria-label="Permalink to &quot;青菜丸子粉丝汤@nz&quot;">​</a></h1><h2 id="备料" tabindex="-1">备料 <a class="header-anchor" href="#备料" aria-label="Permalink to &quot;备料&quot;">​</a></h2><ol><li><p><input type="checkbox"> 肉馅, 尽量新鲜, 瘦:肥 9:1, 肉馅不应该均质, 应该有颗粒感, 也不应该过细, 鱼丸一样的肉丸不会好吃.</p></li><li><p><input type="checkbox"> 上海青</p></li><li><p><input type="checkbox"> 粉丝, 提前泡开</p></li><li><p><input type="checkbox"> 鸡蛋</p></li><li><p><input type="checkbox"> 姜, 凿子或药船, 制成糜</p></li><li><p><input type="checkbox"> 葱, 切葱圈</p></li><li><p><input type="checkbox"> 生抽</p></li><li><p><input type="checkbox"> 大红袍花椒粉</p></li><li><p><input type="checkbox"> 白胡椒粉</p></li><li><p><input type="checkbox"> 盐</p></li></ol><h2 id="烹调工序" tabindex="-1">烹调工序 <a class="header-anchor" href="#烹调工序" aria-label="Permalink to &quot;烹调工序&quot;">​</a></h2><ol><li><p>300g肉馅放入一个鸡蛋的, 放一些姜糜, 大红袍花椒粉, 一些白胡椒粉, 少量盐, 一些生抽, 顺一个方向搅拌, 上劲, 搅拌的越狠, 后面的丸子吃起来越紧实. 但这道菜里我个人更喜欢稍松散,略上劲的肉丸子.</p></li><li><p>上海青焯水后捞出</p></li><li><p>另烧水, 水开用勺子或手捏肉馅,成为丸子,让丸子下锅, 途中撇去浮沫.</p></li><li><p>下完丸子稍微定型就可以下青菜和粉丝了.</p></li><li><p>再煮个三五分钟就可以盛出来了, 简单放些葱圈,醋和生抽<br> 也可以依照个人口味添加其他诸如 香油,耗油,辣椒油,白胡椒粉,香菜末,虾皮,炸腐竹 之类的.</p></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>汤清,味鲜,肉香,粉丝过瘾,青菜健康.<br> just enjoy it.</p></div>',6)]))}const d=t(a,[["render",o]]);export{u as __pageData,d as default};
